import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:8080/';

class CourseService {
    addCourse(data){
        return axios.post(API_URL+ "courses",data,{ headers: authHeader() })
    }
}
export default new CourseService();
